package Study.Practice;

public class Circle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double pi = 3.14;
		double radius = 5.0;
		
		double circle = 2 * pi * radius;
		
		System.out.println("Circumference is = " + circle + " Feet");
		
		System.out.println("Area is = " + (pi * Math.pow(radius, 2)) + " Feet");
	}

}
